import React, { useState, useEffect } from "react";
import BotCollection from "./BotCollection";
import YourBotArmy from "./YourBotArmy";

const App = () => {
  const [enlistedBots, setEnlistedBots] = useState([]);
  const [allBots, setAllBots] = useState([]);

  useEffect(() => {
    fetch("https://botdata.onrender.com/bots")
      .then((response) => response.json())
      .then((data) => setAllBots(data))
      .catch((error) => console.error("Error fetching bots:", error));
  }, []);

  const onEnlist = (bot) => {
    setEnlistedBots((prevEnlistedBots) => [...prevEnlistedBots, bot]);
  };

  const onRelease = (bot) => {
    setEnlistedBots((prevEnlistedBots) => prevEnlistedBots.filter((b) => b.id !== bot.id));
  };

  const onDischarge = () => {
    setEnlistedBots([]);
  };

  return (
    <div className="app">
      <BotCollection bots={allBots} onEnlist={onEnlist} />
      <YourBotArmy enlistedBots={enlistedBots} onRelease={onRelease} onDischarge={onDischarge} />
    </div>
  );
};

export default App;
