import React, { useState, useEffect } from 'react';
import CustomBotCollection from './CustomBotCollection';
import YourCustomBotArmy from './YourCustomBotArmy';
import CustomSortBar from './CustomSortBar';
import './CustomApp.css';

function CustomApp() {
  // Declared variables for rendering the bots
  const [allBots, setAllBots] = useState([]);
  const [customBotArmy, setCustomBotArmy] = useState([]);
  const [customSortBy, setCustomSortBy] = useState(null);

  // Fetching the bot data from a different API
  useEffect(() => {
    fetch('https://custom-bot-api.example.com/bots')
      .then((response) => response.json())
      .then((data) => setAllBots(data))
      .catch((error) => console.error('Error fetching data:', error));
  }, []);

  // Function to add a bot to the customBotArmy
  const addToCustomBotArmy = (bot) => {
    // Check if the bot is not already in the customBotArmy before adding
    if (!customBotArmy.find((b) => b.id === bot.id)) {
      setCustomBotArmy([...customBotArmy, bot]);
    }
  };

  // Function to remove a bot from the customBotArmy
  const removeFromCustomBotArmy = (bot) => {
    setCustomBotArmy(customBotArmy.filter((b) => b.id !== bot.id));
  };

  // Function to handle bot deletion from the deployed API
  const handleCustomBotDeletion = (botId) => {
    fetch(`https://custom-bot-api.example.com/bots/${botId}`, {
      method: 'DELETE',
    })
      .then((response) => response.json())
      .then(() => {
        setAllBots((prevBots) =>
          prevBots.filter((bot) => bot.id !== botId)
        );
      })
      .catch((error) => console.error('Error deleting bots:', error));
  };

  // Function to handle sorting
  const handleCustomSortChange = (sortBy) => {
    setCustomSortBy(sortBy);
  };

  return (
    // Rendering bots on the web page
    <div className="CustomApp">
      <YourCustomBotArmy
        allBots={allBots}
        customBotArmy={customBotArmy}
        removeFromCustomArmy={removeFromCustomBotArmy}
        customBotDeletion={handleCustomBotDeletion}
      />
      <CustomSortBar onCustomSortChange={handleCustomSortChange} />
      <CustomBotCollection
        allBots={allBots}
        addToCustomArmy={addToCustomBotArmy}
        customSortBy={customSortBy}
      />
    </div>
  );
}

export default CustomApp;
