import React from "react";

const Bot = ({ bot, onEnlist, onRelease, onDischarge }) => {
  const { id, name, health, damage, armor, bot_class, catchphrase, avatar_url } = bot;

  const handleEnlistClick = () => {
    onEnlist(bot);
  };

  const handleReleaseClick = () => {
    onRelease(bot);
  };

  const handleDischargeClick = () => {
    onDischarge(bot);
  };

  return (
    <div className="bot-card">
      <img src={avatar_url} alt={name} />
      <h3>{name}</h3>
      <p>Health: {health}</p>
      <p>Damage: {damage}</p>
      <p>Armor: {armor}</p>
      <p>Class: {bot_class}</p>
      <p>Catchphrase: {catchphrase}</p>
      {onEnlist && <button onClick={handleEnlistClick}>Enlist</button>}
      {onRelease && <button onClick={handleReleaseClick}>Release</button>}
      {onDischarge && <button className="discharge-button" onClick={handleDischargeClick}>Discharge</button>}
      <p>Bot ID: {id}</p> {/* You can use the id variable here if needed */}
    </div>
  );
};

export default Bot;
