import React, { useState } from "react";
import BotCollection from "./BotCollection";
import YourBotArmy from "./YourBotArmy";
import BotCard from   "./BotCard"

const App = () => {
  const [enlistedBots, setEnlistedBots] = useState([]);

  const handleEnlist = (bot) => {
    setEnlistedBots([...enlistedBots, bot]);
  };

  const handleRelease = (bot) => {
    setEnlistedBots(enlistedBots.filter((enlistedBot) => enlistedBot.id !== bot.id));
  };

  const handleDischarge = (bot) => {
    // Call the API DELETE endpoint to remove the bot permanently
    // For this example, we'll simulate the delete by just removing it from enlistedBots
    setEnlistedBots(enlistedBots.filter((enlistedBot) => enlistedBot.id !== bot.id));
  };

  return (
    <div className="app">
      <h1>Bot Army Manager</h1>
      <BotCollection onEnlist={handleEnlist} />
      <YourBotArmy enlistedBots={enlistedBots} onRelease={handleRelease} onDischarge={handleDischarge} />
    </div>
  );
};

export default App;
