import React from 'react';
import BotCard from './BotCard';
import { useBotContext } from './BotProvider';

const YourBotArmy = () => {
  const { enlistedBots, releaseBot, dischargeBot } = useBotContext();

  // Now the variables are declared and used
  const handleReleaseBot = (bot) => {
    releaseBot(bot);
  };

  const handleDischargeBot = (bot) => {
    dischargeBot(bot);
  };

  return (
    <div className="your-bot-army">
      <h2>Your Bot Army</h2>
      {enlistedBots.map((bot) => (
        <BotCard
          key={bot.id}
          bot={bot}
          handleClick={() => handleReleaseBot(bot)} // Use the declared function
          showDeleteButton
          handleDelete={() => handleDischargeBot(bot)} // Use the declared function
        />
      ))}
    </div>
  );
};

export default YourBotArmy;
