import React from 'react';
import { useBotContext } from './BotContext';

const BotCard = ({ bot }) => {
  const { enlistBot, releaseBot } = useBotContext();
  const { name, health, damage, armor, bot_class, catchphrase, avatar_url } = bot;

  const handleClick = () => {
    if (bot.enlisted) {
      releaseBot(bot);
    } else {
      enlistBot(bot);
    }
  };

  return (
    <div className="bot-card">
      <img src={avatar_url} alt={name} />
      <h3>{name}</h3>
      <p>Health: {health}</p>
      <p>Damage: {damage}</p>
      <p>Armor: {armor}</p>
      <p>Class: {bot_class}</p>
      <p>Catchphrase: {catchphrase}</p>
      <button onClick={handleClick}>{bot.enlisted ? 'Release' : 'Enlist'}</button>
    </div>
  );
};

export default BotCard;
