import React, { createContext, useState, useEffect, useContext } from 'react';
import axios from 'axios';

const BotContext = createContext();

export const useBotContext = () => {
  const context = useContext(BotContext);
  if (!context) {
    throw new Error('useBotContext must be used within a BotProvider');
  }
  return context;
};

const BotProvider = ({ children }) => {
  const [botData, setBotData] = useState([]);
  const [enlistedBots, setEnlistedBots] = useState([]);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await axios.get('https://botdata.onrender.com/bots/');
      setBotData(response.data);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const enlistBot = (bot) => {
    if (!enlistedBots.some((enlistedBot) => enlistedBot.bot_class === bot.bot_class)) {
      setEnlistedBots([...enlistedBots, { ...bot, enlisted: true }]);
    }
  };

  const releaseBot = (bot) => {
    const updatedEnlistedBots = enlistedBots.map((b) => (b.id === bot.id ? { ...b, enlisted: false } : b));
    setEnlistedBots(updatedEnlistedBots);
  };

  const dischargeBot = async (bot) => {
    try {
      await axios.delete(`https://botdata.onrender.com/bots/${bot.id}`);
      setEnlistedBots(enlistedBots.filter((b) => b.id !== bot.id));
    } catch (error) {
      console.error('Error discharging bot:', error);
    }
  };

  const values = {
    botData,
    enlistedBots,
    enlistBot,
    releaseBot,
    dischargeBot,
  };

  return (
    <BotContext.Provider value={values}>
      {children}
    </BotContext.Provider>
  );
};

export default BotProvider;
