import React, { useState } from 'react';
import BotCard from './BotCard';
import { useBotContext } from './BotProvider';

const BotCollection = () => {
  const { botData } = useBotContext();
  const [filterClass, setFilterClass] = useState('');

  const filteredBots = filterClass ? botData.filter((bot) => bot.bot_class === filterClass) : botData;

  return (
    <div>
      <h2>Available Bots</h2>
      <div>
        <label htmlFor="filter">Filter by Class: </label>
        <select id="filter" onChange={(e) => setFilterClass(e.target.value)}>
          <option value="">All</option>
          <option value="Support">Support</option>
          <option value="Medic">Medic</option>
          <option value="Assault">Assault</option>
          <option value="Defender">Defender</option>
          <option value="Captain">Captain</option>
          <option value="Witch">Witch</option>
        </select>
      </div>
      <div>
        {filteredBots.map((bot) => (
          <BotCard key={bot.id} bot={bot} />
        ))}
      </div>
    </div>
  );
};

export default BotCollection;
